import prize02 from '@/assets/images/prize-02@2x.png';
import prize03 from '@/assets/images/prize-03@2x.png';

export default {
  props: {
    imageName: {
      type: String,
      default: '',
    },
    title: {
      type: String,
      default: '',
    },
    text: {
      type: String,
      default: '',
    },
  },
  data() {
    return {
      prize01,
      prize02,
      prize03,
    };
  },
  computed: {
    isMobile() {
      return this.$mq === 'mobile';
    },
    isDesktop() {
      return this.$mq === 'desktop';
